mapper.insert:
insert into ACTIVITY_INSTANCE (activity_id, user_id, is_active, current_task_id) values (
	#{activityId}, #{userId}, true, #{firstTask}
)

mapper.select:
		select * from TASK_DEFINITION where id in (
            select next_task from ACTIVITY_TASK_RELATION 
            where activity_id = #{activityId} and task_id = #{taskId} and branch_code = #{branchCode}
        )
